sql.where:
deleteyn = 'N'


	(
		title LIKE '%' || #{keyword} || '%'
		OR
		content LIKE '%' || #{keyword} || '%'
	)



	 mid = #{mid} 


	 category_no = #{category_no}

mapper.select:
SELECT *
FROM (
	SELECT good_board.*, ROW_NUMBER() OVER (ORDER BY bno DESC) AS rnum
	FROM good_board

)


	rnum > #{skip}
	AND rnum <= #{skip} + #{size}


ORDER BY bno DESC

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM good_likeboard
WHERE mid = #{mid, jdbcType=VARCHAR}
AND bno = #{bno, jdbcType=NUMERIC}
AND deleteyn = 'N'
and like_board = 1

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM good_likeboard
WHERE mid = #{mid, jdbcType=VARCHAR}
AND bno = #{bno, jdbcType=NUMERIC}
AND deleteyn = 'N'
and like_board = 0

mapper.update:
UPDATE good_likeboard
SET deleteyn = #{deleteYN}, LIKE_BOARD_DATE = SYSDATE
WHERE mid = #{mid, jdbcType=VARCHAR}
AND bno = #{bno, jdbcType=NUMERIC}

UPDATE good_board
SET like_cnt = like_cnt + #{likeValue}
WHERE bno = #{bno, jdbcType=NUMERIC}

mapper.select:
SELECT like_cnt
FROM good_board
WHERE bno = #{bno, jdbcType=NUMERIC}

SELECT hate_cnt
FROM good_board
WHERE bno = #{bno, jdbcType=NUMERIC}
