mapper.select:

SELECT * 
FROM (
    SELECT A.*, ROWNUM rnum 
    FROM (
        SELECT * 
        FROM good_member 
        WHERE deleteYn = 'N'
        ORDER BY mid
    ) A 
    WHERE ROWNUM <= #{page} * #{size}
) 
WHERE rnum > (#{page} - 1) * #{size}

mapper.delete:
UPDATE good_member
SET deleteYn = 'Y'
WHERE mid = #{mid}

mapper.update:
UPDATE good_member
SET auto_Login = #{auto_Login}
WHERE mid = #{mid} AND deleteYn = 'N'
